package project.spring.securityjwt.controller;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.security.authentication.AuthenticationManager;import org.springframework.security.authentication.UsernamePasswordAuthenticationToken;import org.springframework.web.bind.annotation.*;import project.spring.securityjwt.model.AuthRequest;import project.spring.securityjwt.util.JwtUtil;@RestControllerpublic class ApplicationController {    @Autowired    private JwtUtil jwtUtil;    @Autowired    private AuthenticationManager authenticationManager;    @GetMapping("/")    public String welcome() {        return "Welcome to my-App";    }    @PostMapping("/auth")    public String generateToken(@RequestBody AuthRequest authRequest) throws Exception {        try {            authenticationManager.authenticate(                    new UsernamePasswordAuthenticationToken(authRequest.getUsername(),authRequest.getPassword())            );        } catch (Exception e) {            throw new Exception("invalid username/password");        }        return jwtUtil.generateToken(authRequest.getUsername());    }}