package project.spring.securityjwt.model;import lombok.*;import javax.persistence.*;import java.io.Serializable;import java.util.List;@Getter@Setter@AllArgsConstructor@NoArgsConstructor@Builder@Entity@Table(name = "users")public class User implements Serializable {    @Id    @GeneratedValue(strategy = GenerationType.IDENTITY)    @Column(name = "id")    private Long id;    @Column(name = "username")    private String username;    @Column(name = "password")    private String password;    @Column(name = "name")    public String name;    @Column(name = "email")    private String email;    @Column(name = "active")    public Boolean active;    @ManyToMany(fetch = FetchType.LAZY)    @JoinTable(name = "user_role",            joinColumns = @JoinColumn(name = "user_id"),            inverseJoinColumns = @JoinColumn(name = "role_id"),            uniqueConstraints = {@UniqueConstraint(columnNames = {"user_id", "role_id"})}    )    public List<Role> roles;    private static final long serialVersionUID = 1L;}